import React, { useState, useEffect, useRef } from 'react';
import profilePic from './assets/port profile.jpg';
import './index.css';

const App = () => {
  const [showCerts, setShowCerts] = useState(false);
  const [zoomImage, setZoomImage] = useState(null);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState([
    { id: 1, text: "Hello there! ðŸ‘‹ I'm your friendly AI assistant for Saikrishna's portfolio. Feel free to say hi, ask me anything about his skills, projects, experience, or just have a casual chat! ðŸ˜Š", isBot: true }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showTableOfContents, setShowTableOfContents] = useState(false);
  const [currentSection, setCurrentSection] = useState(0);
  const mainRef = useRef(null);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!mainRef.current) return;
      if (e.key === 'ArrowRight') {
        mainRef.current.scrollBy({ left: window.innerWidth, behavior: 'smooth' });
      } else if (e.key === 'ArrowLeft') {
        mainRef.current.scrollBy({ left: -window.innerWidth, behavior: 'smooth' });
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Chat functionality
  const processUserQuery = (query) => {
    const lowerQuery = query.toLowerCase();

    // General greetings and casual conversation
    if (lowerQuery.includes('hi') || lowerQuery.includes('hello') || lowerQuery.includes('hey') || lowerQuery.includes('hlo') || lowerQuery.includes('hii')) {
      const greetings = [
        "Hello there! ðŸ‘‹ How can I help you today?",
        "Hi! ðŸ˜Š Great to see you! What would you like to know about Saikrishna?",
        "Hey! ðŸ‘‹ Welcome to Saikrishna's portfolio! How can I assist you?",
        "Hello! ðŸŒŸ Nice to meet you! Feel free to ask me anything about Saikrishna's work and experience."
      ];
      return greetings[Math.floor(Math.random() * greetings.length)];
    }

    if (lowerQuery.includes('good morning') || lowerQuery.includes('good afternoon') || lowerQuery.includes('good evening') || lowerQuery.includes('good night')) {
      const timeGreetings = [
        "Good morning! â˜€ï¸ Hope you're having a wonderful day! How can I help you?",
        "Good afternoon! ðŸŒ¤ï¸ Thanks for stopping by! What would you like to know?",
        "Good evening! ðŸŒ™ Great to see you here! How can I assist you today?",
        "Good night! ðŸŒ™ Sweet dreams! Feel free to ask me anything about Saikrishna's portfolio."
      ];
      return timeGreetings[Math.floor(Math.random() * greetings.length)];
    }

    if (lowerQuery.includes('how are you') || lowerQuery.includes('how r u') || lowerQuery.includes('how do you do')) {
      return "I'm doing great, thank you! ðŸ˜Š I'm excited to help you learn more about Saikrishna's amazing portfolio. What interests you most?";
    }

    if (lowerQuery.includes('thank') || lowerQuery.includes('thanks') || lowerQuery.includes('thx')) {
      return "You're very welcome! ðŸ˜Š I'm here to help. Is there anything else you'd like to know about Saikrishna?";
    }

    if (lowerQuery.includes('bye') || lowerQuery.includes('goodbye') || lowerQuery.includes('see you') || lowerQuery.includes('take care')) {
      return "Goodbye! ðŸ‘‹ It was great chatting with you! Feel free to come back anytime if you have more questions about Saikrishna's portfolio. Have a wonderful day! âœ¨";
    }

    if (lowerQuery.includes('who are you') || lowerQuery.includes('what are you') || lowerQuery.includes('tell me about yourself')) {
      return "I'm Saikrishna's AI Portfolio Assistant! ðŸ¤–âœ¨ I'm here to help visitors learn about his skills, projects, experience, and help them connect with him. I'm knowledgeable about everything in his portfolio and love having conversations!";
    }

    if (lowerQuery.includes('nice') || lowerQuery.includes('cool') || lowerQuery.includes('awesome') || lowerQuery.includes('amazing')) {
      return "Thank you! ðŸ˜Š I'm glad you think so! Saikrishna has put a lot of effort into creating this portfolio. Is there something specific you'd like to explore?";
    }

    // Portfolio-specific queries
    if (lowerQuery.includes('skill') || lowerQuery.includes('technology') || lowerQuery.includes('programming')) {
      return "Saikrishna has strong skills in Java, Python, C++, HTML/CSS/JavaScript, MySQL, OpenStack, GCP, GitHub, Linux, and VS Code. He's particularly interested in Cloud, DevOps, and Machine Learning! ðŸš€";
    }

    if (lowerQuery.includes('project') || lowerQuery.includes('work')) {
      return "Saikrishna has worked on several exciting projects: 1) ðŸ§  Brain Tumor Classification using ResNet50 (in progress), 2) ðŸ¡ Homestay Management System (completed), and 3) ðŸ“š Quiz Web Application with Django (completed). All projects are available on his GitHub!";
    }

    if (lowerQuery.includes('experience') || lowerQuery.includes('background')) {
      return "Saikrishna is a passionate Computer Science student (2022-2026) at Adichunchanagiri Institute of Technology. He has experience in hackathons, IEEE conference volunteering, and has earned certifications in Java, Machine Learning, and Frontend Development. Quite impressive, right? ðŸ˜Š";
    }

    if (lowerQuery.includes('contact') || lowerQuery.includes('email') || lowerQuery.includes('linkedin')) {
      return "You can contact Saikrishna at vishwakarmasaikrishna@gmail.com, connect on LinkedIn (linkedin.com/in/saikrishna-badiger-063a9a314), or check out his GitHub (github.com/sai-krishna26). He's always open to opportunities and meaningful conversations! ðŸ“§";
    }

    if (lowerQuery.includes('resume') || lowerQuery.includes('cv')) {
      return "Saikrishna's resume is available for download on his portfolio! ðŸ“„ Click the 'Download Resume' button to get his latest CV with all his skills, projects, and experience. It's quite comprehensive!";
    }

    // Default response for unrecognized queries
    const defaultResponses = [
      "That's an interesting question! ðŸ¤” While I'm primarily designed to help with Saikrishna's portfolio, I'd be happy to chat about his skills, projects, experience, or help you connect with him. What would you like to know?",
      "I'm not quite sure about that, but I'd love to tell you about Saikrishna's amazing work! ðŸ˜Š You can ask me about his skills, projects, experience, or how to contact him.",
      "Interesting! ðŸ¤” I'm here to help you learn about Saikrishna's portfolio. Feel free to ask about his skills, projects, experience, or contact information!",
      "That's a great question! ðŸŒŸ While I'm focused on helping with Saikrishna's portfolio, I'd be happy to chat about his work, skills, or help you connect with him. What interests you most?"
    ];
    return defaultResponses[Math.floor(Math.random() * defaultResponses.length)];
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMessage = { id: Date.now(), text: inputMessage, isBot: false };
    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    // Simulate AI response delay
    setTimeout(() => {
      const botResponse = processUserQuery(inputMessage);
      const botMessage = { id: Date.now() + 1, text: botResponse, isBot: true };
      setMessages(prev => [...prev, botMessage]);
      setIsLoading(false);
    }, 1000);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  // Navigation functions
  const scrollToSection = (sectionIndex) => {
    if (mainRef.current) {
      const sectionWidth = window.innerWidth;
      mainRef.current.scrollTo({
        left: sectionWidth * sectionIndex,
        behavior: 'smooth'
      });
      setCurrentSection(sectionIndex);
    }
  };

  const handleScroll = () => {
    if (mainRef.current) {
      const scrollLeft = mainRef.current.scrollLeft;
      const sectionWidth = window.innerWidth;
      const currentIndex = Math.round(scrollLeft / sectionWidth);
      setCurrentSection(currentIndex);
    }
  };

  useEffect(() => {
    if (mainRef.current) {
      mainRef.current.addEventListener('scroll', handleScroll);
      return () => mainRef.current?.removeEventListener('scroll', handleScroll);
    }
  }, []);

  return (
    <div className="min-h-screen bg-black text-white font-['Poppins'] overflow-hidden">
      {/* Fixed Navigation Bar */}
      <nav className="fixed top-0 left-0 right-0 z-50 bg-black/90 backdrop-blur-md border-b border-yellow-500/30">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            {/* Logo/Name */}
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-yellow-400 to-amber-600 rounded-full flex items-center justify-center">
                <span className="text-black font-bold text-sm">SB</span>
              </div>
              <span className="text-yellow-400 font-bold text-lg">Saikrishna Badiger</span>
            </div>

            {/* Navigation Links */}
            <div className="hidden md:flex items-center space-x-8">
              {['Bio', 'About', 'Skills', 'Projects', 'Certifications', 'Contact', 'Achievements'].map((section, index) => (
                <button
                  key={section}
                  onClick={() => scrollToSection(index)}
                  className={`text-sm font-medium transition-colors duration-300 hover:text-yellow-400 ${
                    currentSection === index ? 'text-yellow-400 border-b-2 border-yellow-400' : 'text-gray-300'
                  }`}
                >
                  {section}
                </button>
              ))}
            </div>

            {/* Table of Contents Button */}
            <button
              onClick={() => setShowTableOfContents(!showTableOfContents)}
              className="md:hidden bg-gradient-to-r from-yellow-400 to-amber-600 text-black px-3 py-2 rounded-lg font-medium hover:from-yellow-500 hover:to-amber-700 transition-all duration-300"
            >
              â˜° Menu
            </button>
          </div>
        </div>
      </nav>

      {/* Section Indicators (Progress Bar) */}
      <div className="fixed top-16 left-0 right-0 z-40 bg-black/50 backdrop-blur-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-1 py-2">
            {['Bio', 'About', 'Skills', 'Projects', 'Certifications', 'Contact', 'Achievements'].map((section, index) => (
              <button
                key={section}
                onClick={() => scrollToSection(index)}
                className={`flex-1 h-1 rounded-full transition-all duration-300 ${
                  currentSection === index 
                    ? 'bg-gradient-to-r from-yellow-400 to-amber-600' 
                    : 'bg-gray-600 hover:bg-gray-500'
                }`}
                title={`Go to ${section}`}
              />
            ))}
          </div>
        </div>
      </div>

      <main
        ref={mainRef}
        className="w-screen h-screen overflow-x-auto overflow-y-hidden flex snap-x snap-mandatory scroll-smooth pt-16"
      >
        {/* ðŸ”¹ Bio Slide */}
        <section className="min-w-full h-full snap-start flex flex-col justify-center items-center text-center px-4 py-8 sm:px-6 bg-black">
          <div className="relative group">
            <img
              src={profilePic}
              alt="Saikrishna Badiger"
              className="w-40 sm:w-48 h-40 sm:h-48 object-cover rounded-full shadow-2xl border-4 border-yellow-400 shadow-yellow-400/50 transition-all duration-700 group-hover:scale-105 group-hover:shadow-yellow-400/80"
            />
            <div className="absolute inset-0 rounded-full bg-gradient-to-r from-yellow-400/10 to-amber-600/10 opacity-0 group-hover:opacity-100 transition-opacity duration-700"></div>
            <div className="absolute -inset-2 rounded-full bg-gradient-to-r from-yellow-400/20 to-amber-600/20 blur-xl opacity-0 group-hover:opacity-100 transition-all duration-700 animate-pulse"></div>
          </div>
          <h1 className="text-3xl sm:text-5xl font-extrabold bg-gradient-to-r from-yellow-400 to-amber-600 bg-clip-text text-transparent drop-shadow-lg mt-6">
            Saikrishna Badiger
          </h1>
          <p className="text-sm sm:text-lg text-gray-200 italic mt-2">
            "Turning data into decisions | Code into creations"
          </p>
          <p className="text-base sm:text-xl mt-3 text-gray-200">Aspiring Data Analyst | Cloud | DevOps | Web</p>
          <span className="mt-4 inline-block bg-gradient-to-r from-yellow-400 to-amber-600 text-black font-bold px-4 py-2 rounded-xl text-sm shadow-lg hover:shadow-yellow-400/70 transition-all duration-300 transform hover:scale-105">
            ðŸš€ Open to Work & Internships
          </span>

          <p className="mt-3 text-xs sm:text-sm text-gray-400 animate-pulse">ðŸ‘‰ Swipe or use â¬…ï¸âž¡ï¸ keys</p>
          <div className="mt-5 flex flex-wrap justify-center gap-4 text-sm font-medium">
            <a href="mailto:vishwakarmasaikrishna@gmail.com" className="text-yellow-400 hover:text-amber-500 transition-colors duration-300 hover:underline">Email</a>
            <a href="https://github.com/sai-krishna26" className="text-yellow-400 hover:text-amber-500 transition-colors duration-300 hover:underline" target="_blank" rel="noreferrer">GitHub</a>
            <a href="https://linkedin.com/in/saikrishna-badiger-063a9a314" className="text-yellow-400 hover:text-amber-500 transition-colors duration-300 hover:underline" target="_blank" rel="noreferrer">LinkedIn</a>
          </div>
          <p className="mt-3 text-sm text-gray-400 italic max-w-md">
            ðŸ“© Click on the Gmail link to connect directly â€” I'm always open to opportunities and meaningful conversations!
          </p>
          <a
            href={`${process.env.PUBLIC_URL}/SKB resume.pdf`}
            download
            className="mt-5 inline-block bg-gradient-to-r from-yellow-400 to-amber-600 hover:from-yellow-500 hover:to-amber-700 text-black font-bold px-8 py-3 rounded-xl shadow-lg hover:shadow-yellow-400/70 transition-all duration-300 transform hover:scale-105 text-sm sm:text-base"
          >
            ðŸ“„ Download Resume
          </a>
        </section>

        {/* ðŸ”¹ About Slide */}
