import React, { useState, useEffect, useRef } from 'react';
import profilePic from './assets/profile.jpg';
import './index.css';

const App = () => {
  const [showCerts, setShowCerts] = useState(false);
  const [zoomImage, setZoomImage] = useState(null);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: 'Hello! I\'m your portfolio assistant. I can help you learn more about Saikrishna Badiger, his skills, projects, and experience. How can I help you today?'
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const mainRef = useRef(null);
  const chatEndRef = useRef(null);

  // Scroll to bottom of chat when new messages arrive
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!mainRef.current) return;
      if (e.key === 'ArrowRight') {
        mainRef.current.scrollBy({ left: window.innerWidth, behavior: 'smooth' });
      } else if (e.key === 'ArrowLeft') {
        mainRef.current.scrollBy({ left: -window.innerWidth, behavior: 'smooth' });
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  return (
    <div className="min-h-screen bg-sky-900 text-white font-['Poppins'] overflow-hidden">
      <main
        ref={mainRef}
        className="w-screen h-screen overflow-x-auto overflow-y-hidden flex snap-x snap-mandatory scroll-smooth"
      >
        {/*  Bio Slide */}
        <section className="min-w-full h-full snap-start flex flex-col justify-center items-center text-center px-4 py-8 sm:px-6 bg-sky-900">
          <img
            src={profilePic}
            alt="Saikrishna Badiger"
            className="w-40 sm:w-48 h-52 sm:h-60 object-cover rounded-xl shadow-2xl border-4 border-blue-400 mb-4"
          />
          <h1 className="text-3xl sm:text-5xl font-extrabold bg-gradient-to-r from-white to-blue-300 text-transparent bg-clip-text drop-shadow-lg">
            Saikrishna Badiger
          </h1>
          <p className="text-sm sm:text-lg text-blue-200 italic mt-1">
            "Turning data into decisions | Code into creations"
          </p>
          <p className="text-base sm:text-xl mt-2 text-blue-200">Aspiring Data Analyst | Cloud | DevOps | Web</p>
          <span className="mt-3 inline-block bg-green-300 text-black font-semibold px-3 py-1 rounded-full text-xs">
             Open to Work & Internships
          </span>

          <p className="mt-2 text-xs sm:text-sm text-blue-300 animate-pulse"> Swipe or use  keys</p>
          <div className="mt-4 flex flex-wrap justify-center gap-4 text-sm font-medium">
            <a href="mailto:vishwakarmasaikrishna@gmail.com" className="text-blue-300 hover:underline">Email</a>
            <a href="https://github.com/sai-krishna26" className="text-blue-300 hover:underline" target="_blank" rel="noreferrer">GitHub</a>
            <a href="https://linkedin.com/in/saikrishna-badiger-063a9a314" className="text-blue-300 hover:underline" target="_blank" rel="noreferrer">LinkedIn</a>
          </div>
          <p className="mt-2 text-sm text-blue-300 italic">
             Click on the Gmail link to connect directly  I'm always open to opportunities and meaningful conversations!
          </p>
          <a
            href={`${process.env.PUBLIC_URL}/SKB resume.pdf`}
            download
            className="mt-4 inline-block bg-blue-400 hover:bg-blue-500 text-black font-semibold px-6 py-2 rounded shadow transition text-sm sm:text-base"
          >
             Download Resume
          </a>
        </section>

        {/*  About Slide */}
        <section className="min-w-full h-full snap-start flex justify-center items-center p-10 bg-sky-900">
          <div className="max-w-2xl text-center space-y-4">
          <h2 className="text-3xl font-semibold border-b border-blue-300 pb-1 mb-4 text-blue-300">About Me</h2>

          <p className="text-blue-100 leading-relaxed text-sm sm:text-base">
            Im a passionate Computer Science student (20222026) at Adichunchanagiri Institute of Technology with strong foundations in Java, Python, Data Structures, and Web Development.
          </p>

          <p className="text-blue-100 leading-relaxed text-sm sm:text-base">
            I enjoy building real-world software, solving complex problems, and constantly learning new tools  especially in the fields of <span className="text-blue-300 font-medium">Cloud</span>, <span className="text-blue-300 font-medium">DevOps</span>, and <span className="text-blue-300 font-medium">Machine Learning</span>.
          </p>

          <p className="text-blue-100 leading-relaxed text-sm sm:text-base">
            Im seeking exciting opportunities where I can grow as a <span className="text-blue-300 font-medium">Data Analyst or Software Developer</span>, contribute to impactful projects, and collaborate in dynamic teams.
          </p>

          <p className="text-blue-300 italic text-sm sm:text-base">
            What sets me apart is my curiosity-driven learning and hands-on experience building end-to-end projects.
          </p>
          </div>
        </section>


        {/*  Skills Slide */}
<section className="min-w-full h-full snap-start flex justify-center items-center p-10 bg-sky-900">
  <div className="max-w-4xl w-full">
    <h2 className="text-3xl font-semibold text-blue-300 mb-6 text-center">Skills</h2>

    {/* Technical Skills */}
    <div className="mb-8">
      <h3 className="text-xl text-blue-200 mb-2">Technical Skills</h3>
      <ul className="grid grid-cols-2 sm:grid-cols-3 gap-3 text-blue-100 text-sm sm:text-base list-disc list-inside">
        <li>Java, Python, C++, C</li>
        <li>DSA</li>
        <li>HTML, CSS, JavaScript</li>
        <li>MySQL</li>
        <li>OpenStack, GCP</li>
