import React, { useState, useEffect, useRef } from 'react';
import profilePic from './assets/profile.jpg';
import './index.css';

const App = () => {
  const [darkMode, setDarkMode] = useState(false);
  const mainRef = useRef(null);

  // Load saved theme from localStorage
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') setDarkMode(true);
  }, []);

  // Apply theme toggle
  useEffect(() => {
    document.documentElement.classList.toggle('dark', darkMode);
    localStorage.setItem('theme', darkMode ? 'dark' : 'light');
  }, [darkMode]);

  // Keyboard slide navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!mainRef.current) return;
      if (e.key === 'ArrowRight') {
        mainRef.current.scrollBy({ left: window.innerWidth, behavior: 'smooth' });
      } else if (e.key === 'ArrowLeft') {
        mainRef.current.scrollBy({ left: -window.innerWidth, behavior: 'smooth' });
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  return (
    <div className="min-h-screen bg-sky-900 dark:bg-gray-900 text-white dark:text-gray-100 font-['Poppins'] overflow-hidden">

      {/* üåó Theme Toggle Button */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="fixed top-4 right-4 z-50 text-sm sm:text-base bg-white dark:bg-gray-800 dark:text-white text-black px-4 py-2 rounded shadow hover:bg-gray-200 dark:hover:bg-gray-700 transition"
      >
        {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
      </button>

      <main
        ref={mainRef}
        className="w-screen h-screen overflow-x-auto overflow-y-hidden flex snap-x snap-mandatory scroll-smooth"
      >

        {/* Profile Slide */}
        <section className="min-w-full h-full snap-start flex flex-col justify-center items-center text-center px-4 py-8 sm:px-6 bg-sky-900 dark:bg-gray-900">
          <div className="relative group mb-6">
            <img
              src={profilePic}
              alt="Saikrishna Badiger"
              className="w-40 sm:w-48 h-52 sm:h-60 object-cover rounded-xl shadow-2xl border-4 border-blue-400 group-hover:scale-105 transition-transform duration-500"
            />
          </div>
          <h1 className="text-3xl sm:text-5xl font-extrabold bg-gradient-to-r from-white to-blue-300 text-transparent bg-clip-text drop-shadow-lg">
            Saikrishna Badiger
          </h1>
          <p className="text-base sm:text-xl mt-2 sm:mt-3 text-blue-200 dark:text-blue-300">
            Aspiring Data Analyst | Cloud | DevOps | Web
          </p>
          <p className="mt-3 text-xs sm:text-sm text-blue-300 animate-pulse">
            üëâ Swipe or use ‚¨ÖÔ∏è‚û°Ô∏è keys
          </p>
          <div className="mt-5 flex flex-wrap justify-center gap-4 text-sm font-medium">
            <a href="mailto:vishwakarmasaikrishna@gmail.com" className="text-blue-300 hover:underline">Email</a>
            <a href="https://github.com/sai-krishna26" className="text-blue-300 hover:underline" target="_blank" rel="noreferrer">GitHub</a>
            <a href="https://linkedin.com/in/saikrishna-badiger-063a9a314" className="text-blue-300 hover:underline" target="_blank" rel="noreferrer">LinkedIn</a>
          </div>
          <a
            href="/resume.pdf"
            download
            className="mt-5 inline-block bg-blue-400 hover:bg-blue-500 text-black dark:text-white font-semibold px-6 py-2 rounded shadow transition text-sm sm:text-base"
          >
            üìÑ Download Resume
          </a>
        </section>

        {/* About Slide */}
        <section className="min-w-full h-full snap-start flex justify-center items-center p-6 sm:p-10 bg-sky-900 dark:bg-gray-900">
          <div className="max-w-2xl text-center">
            <h2 className="text-3xl font-semibold border-b border-blue-300 pb-1 mb-4 text-blue-300">About Me</h2>
            <p className="leading-relaxed text-base sm:text-lg">
              I'm a Computer Science student at Adichunchanagiri Institute of Technology (2022‚Äì2026),
              with strong foundations in programming, web development, and DevOps.
              I love building impactful software and learning through real-world projects.
            </p>
          </div>
        </section>

        {/* Skills Slide */}
        <section className="min-w-full h-full snap-start flex justify-center items-center p-6 sm:p-10 bg-sky-900 dark:bg-gray-900">
          <div className="max-w-xl">
            <h2 className="text-3xl font-semibold border-b border-blue-300 pb-1 mb-4 text-blue-300">Skills</h2>
            <ul className="grid grid-cols-2 sm:grid-cols-3 gap-3 list-disc list-inside text-blue-100 text-sm sm:text-base">
              <li>Java, Python, C++, C</li>
              <li>DSA</li>
              <li>HTML, CSS, JavaScript</li>
              <li>MySQL</li>
              <li>OpenStack, GCP</li>
              <li>GitHub, Linux</li>
              <li>VS Code, LaTeX</li>
            </ul>
          </div>
        </section>

        {/* Contact Slide */}
        <section className="min-w-full h-full snap-start flex justify-center items-center p-6 sm:p-10 bg-sky-900 dark:bg-gray-900">
          <div className="text-center">
            <h2 className="text-3xl font-semibold border-b border-blue-300 pb-1 mb-4 text-blue-300">Contact</h2>
            <p className="text-blue-100 text-sm sm:text-base">
              üìß Email: <a className="underline text-white" href="mailto:vishwakarmasaikrishna@gmail.com">vishwakarmasaikrishna@gmail.com</a>
            </p>
            <p className="text-blue-100 text-sm sm:text-base">
              üîó GitHub: <a className="underline text-white" href="https://github.com/sai-krishna26">github.com/sai-krishna26</a>
            </p>
            <p className="text-blue-100 text-sm sm:text-base">
              üîó LinkedIn: <a className="underline text-white" href="https://linkedin.com/in/saikrishna-badiger-063a9a314">linkedin.com/in/saikrishna-badiger-063a9a314</a>
            </p>
          </div>
        </section>

      </main>

      <footer className="text-center text-sm text-blue-300 dark:text-gray-400 mt-6">
        &copy; 2025 Saikrishna Badiger. All rights reserved.
      </footer>
    </div>
  );
};

export default App;
