import React, { useState, useEffect, useRef } from 'react';
import profilePic from './assets/port profile.jpg';
import './index.css';

const App = () => {
  const [showCerts, setShowCerts] = useState(false);
  const [zoomImage, setZoomImage] = useState(null);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState([
    { id: 1, text: "Hello! I'm your portfolio AI assistant. Ask me anything about Saikrishna's skills, projects, or experience!", isBot: true }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const mainRef = useRef(null);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!mainRef.current) return;
      if (e.key === 'ArrowRight') {
        mainRef.current.scrollBy({ left: window.innerWidth, behavior: 'smooth' });
      } else if (e.key === 'ArrowLeft') {
        mainRef.current.scrollBy({ left: -window.innerWidth, behavior: 'smooth' });
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Chat functionality
  const processUserQuery = (query) => {
    const lowerQuery = query.toLowerCase();

    if (lowerQuery.includes('skill') || lowerQuery.includes('technology') || lowerQuery.includes('programming')) {
      return "Saikrishna has strong skills in Java, Python, C++, HTML/CSS/JavaScript, MySQL, OpenStack, GCP, GitHub, Linux, and VS Code. He's particularly interested in Cloud, DevOps, and Machine Learning!";
    }

    if (lowerQuery.includes('project') || lowerQuery.includes('work')) {
      return "Saikrishna has worked on several projects: 1) Brain Tumor Classification using ResNet50 (in progress), 2) Homestay Management System (completed), and 3) Quiz Web Application with Django (completed). All projects are available on his GitHub!";
    }

    if (lowerQuery.includes('experience') || lowerQuery.includes('background')) {
      return "Saikrishna is a Computer Science student (2022-2026) at Adichunchanagiri Institute of Technology. He has experience in hackathons, IEEE conference volunteering, and has earned certifications in Java, Machine Learning, and Frontend Development.";
    }

    if (lowerQuery.includes('contact') || lowerQuery.includes('email') || lowerQuery.includes('linkedin')) {
      return "You can contact Saikrishna at vishwakarmasaikrishna@gmail.com, connect on LinkedIn (linkedin.com/in/saikrishna-badiger-063a9a314), or check out his GitHub (github.com/sai-krishna26). He's always open to opportunities!";
    }

    if (lowerQuery.includes('resume') || lowerQuery.includes('cv')) {
      return "Saikrishna's resume is available for download on his portfolio! Click the 'Download Resume' button to get his latest CV with all his skills, projects, and experience.";
    }

    return "I'm here to help! You can ask me about Saikrishna's skills, projects, experience, contact information, or resume. What would you like to know?";
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMessage = { id: Date.now(), text: inputMessage, isBot: false };
    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    // Simulate AI response delay
    setTimeout(() => {
      const botResponse = processUserQuery(inputMessage);
      const botMessage = { id: Date.now() + 1, text: botResponse, isBot: true };
      setMessages(prev => [...prev, botMessage]);
      setIsLoading(false);
    }, 1000);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="min-h-screen bg-black text-white font-['Poppins'] overflow-hidden">
      <main
        ref={mainRef}
        className="w-screen h-screen overflow-x-auto overflow-y-hidden flex snap-x snap-mandatory scroll-smooth"
      >
        {/* ğŸ”¹ Bio Slide */}
        <section className="min-w-full h-full snap-start flex flex-col justify-center items-center text-center px-4 py-8 sm:px-6 bg-black">
          <div className="relative">
            <img
              src={profilePic}
              alt="Saikrishna Badiger"
              className="w-40 sm:w-48 h-40 sm:h-48 object-cover rounded-full shadow-2xl border-4 border-yellow-400 shadow-yellow-400/50 animate-pulse"
            />
            <div className="absolute inset-0 rounded-full bg-gradient-to-r from-yellow-400/20 to-amber-600/20 animate-ping"></div>
          </div>
          <h1 className="text-3xl sm:text-5xl font-extrabold bg-gradient-to-r from-yellow-400 to-amber-600 bg-clip-text text-transparent drop-shadow-lg mt-6">
            Saikrishna Badiger
          </h1>
          <p className="text-sm sm:text-lg text-gray-200 italic mt-2">
            "Turning data into decisions | Code into creations"
          </p>
          <p className="text-base sm:text-xl mt-3 text-gray-200">Aspiring Data Analyst | Cloud | DevOps | Web</p>
          <span className="mt-4 inline-block bg-gradient-to-r from-yellow-400 to-amber-600 text-black font-bold px-4 py-2 rounded-xl text-sm shadow-lg hover:shadow-yellow-400/70 transition-all duration-300 transform hover:scale-105">
            ğŸš€ Open to Work & Internships
          </span>

          <p className="mt-3 text-xs sm:text-sm text-gray-400 animate-pulse">ğŸ‘‰ Swipe or use â¬…ï¸â¡ï¸ keys</p>
          <div className="mt-5 flex flex-wrap justify-center gap-4 text-sm font-medium">
            <a href="mailto:vishwakarmasaikrishna@gmail.com" className="text-yellow-400 hover:text-amber-500 transition-colors duration-300 hover:underline">Email</a>
            <a href="https://github.com/sai-krishna26" className="text-yellow-400 hover:text-amber-500 transition-colors duration-300 hover:underline" target="_blank" rel="noreferrer">GitHub</a>
            <a href="https://linkedin.com/in/saikrishna-badiger-063a9a314" className="text-yellow-400 hover:text-amber-500 transition-colors duration-300 hover:underline" target="_blank" rel="noreferrer">LinkedIn</a>
          </div>
          <p className="mt-3 text-sm text-gray-400 italic max-w-md">
            ğŸ“© Click on the Gmail link to connect directly â€” I'm always open to opportunities and meaningful conversations!
          </p>
          <a
            href={`${process.env.PUBLIC_URL}/SKB resume.pdf`}
            download
            className="mt-5 inline-block bg-gradient-to-r from-yellow-400 to-amber-600 hover:from-yellow-500 hover:to-amber-700 text-black font-bold px-8 py-3 rounded-xl shadow-lg hover:shadow-yellow-400/70 transition-all duration-300 transform hover:scale-105 text-sm sm:text-base"
          >
            ğŸ“„ Download Resume
          </a>
        </section>

        {/* ğŸ”¹ About Slide */}
        <section className="min-w-full h-full snap-start flex justify-center items-center p-10 bg-black">
          <div className="max-w-2xl text-center space-y-6">
            <h2 className="text-3xl font-semibold border-b border-yellow-400 pb-2 mb-6 bg-gradient-to-r from-yellow-400 to-amber-600 bg-clip-text text-transparent">About Me</h2>

            <p className="text-gray-200 leading-relaxed text-sm sm:text-base">
              I'm a passionate Computer Science student (2022â€“2026) at Adichunchanagiri Institute of Technology with strong foundations in Java, Python, Data Structures, and Web Development.
            </p>

            <p className="text-gray-200 leading-relaxed text-sm sm:text-base">
              I enjoy building real-world software, solving complex problems, and constantly learning new tools â€” especially in the fields of <span className="text-yellow-400 font-medium">Cloud</span>, <span className="text-yellow-400 font-medium">DevOps</span>, and <span className="text-yellow-400 font-medium">Machine Learning</span>.
            </p>

            <p className="text-gray-200 leading-relaxed text-sm sm:text-base">
              I'm seeking exciting opportunities where I can grow as a <span className="text-yellow-400 font-medium">Data Analyst or Software Developer</span>, contribute to impactful projects, and collaborate in dynamic teams.
            </p>

            <p className="text-yellow-400 italic text-sm sm:text-base">
              What sets me apart is my curiosity-driven learning and hands-on experience building end-to-end projects.
            </p>
          </div>
        </section>


        {/* ğŸ”¹ Skills Slide */}
        <section className="min-w-full h-full snap-start flex justify-center items-center p-10 bg-black">
          <div className="max-w-4xl w-full">
            <h2 className="text-3xl font-semibold bg-gradient-to-r from-yellow-400 to-amber-600 bg-clip-text text-transparent mb-8 text-center">Skills</h2>

            {/* Technical Skills */}
            <div className="mb-8">
              <h3 className="text-xl text-yellow-400 mb-4">Technical Skills</h3>
              <ul className="grid grid-cols-2 sm:grid-cols-3 gap-4 text-gray-200 text-sm sm:text-base">
                <li className="bg-neutral-900 p-3 rounded-lg border border-yellow-500/30 hover:border-yellow-500/60 transition-all duration-300 hover:shadow-amber-500/40">Java, Python, C++, C</li>
